import React from 'react';

const About = () => {
  return (
    <div className="space-y-6">
      <h2 className="text-3xl font-bold">About Me</h2>
      <p className="text-lg">
        I’m Anmol Sansi, a Software Engineer based in Chicago, IL with 4+ years of experience in building backend systems, cloud-native solutions,
        and AI-powered tools. With a Master’s in Information Technology and a perfect 4.0 GPA from Illinois Institute of Technology, I blend technical
        depth with a passion for problem-solving.
      </p>
      <p className="text-lg">
        My experience spans scalable microservices, RESTful API development, and real-time data pipelines, using technologies like Python, Java,
        AWS, and React. I love building systems that are robust, elegant, and optimized for performance.
      </p>
      <p className="text-lg">
        Outside of work, I enjoy exploring photography, digging into system design challenges, and crafting useful side projects.
      </p>
    </div>
  );
};

export default About;
