import React, { useContext } from 'react';
import { ThemeContext } from '../context/ThemeContext';
import { Link } from 'react-router-dom';

const Sidebar = () => {
  const { darkMode, setDarkMode } = useContext(ThemeContext);

  return (
    <div className="w-64 bg-white dark:bg-gray-800 shadow-md p-4">
      <div className="mb-6">
        <h1 className="text-2xl font-bold text-gray-800 dark:text-white">Anmol Sansi</h1>
        <p className="text-sm text-gray-600 dark:text-gray-300">Software Engineer</p>
        <p className="text-xs text-gray-500 dark:text-gray-400">Chicago, IL</p>
        <a href="mailto:asansi0896@gmail.com" className="text-sm text-blue-500">asansi0896@gmail.com</a>
        <p className="text-sm text-gray-500">(312) 468-3428</p>
        <div className="flex space-x-4 mt-2">
          <a href="https://github.com/anmolsansi" target="_blank" rel="noopener noreferrer" className="text-sm text-blue-500">GitHub</a>
          <a href="https://linkedin.com/in/anmol-sansi" target="_blank" rel="noopener noreferrer" className="text-sm text-blue-500">LinkedIn</a>
        </div>
      </div>
      <nav className="flex flex-col space-y-2">
        <Link to="/" className="text-gray-700 dark:text-gray-300 hover:text-blue-500">About</Link>
        <Link to="/resume" className="text-gray-700 dark:text-gray-300 hover:text-blue-500">Resume</Link>
        <Link to="/projects" className="text-gray-700 dark:text-gray-300 hover:text-blue-500">Projects</Link>
        <Link to="/contact" className="text-gray-700 dark:text-gray-300 hover:text-blue-500">Contact</Link>
      </nav>
      <button
        onClick={() => setDarkMode(!darkMode)}
        className="mt-6 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
      >
        Toggle {darkMode ? 'Light' : 'Dark'} Mode
      </button>
    </div>
  );
};

export default Sidebar;
